<template>
  <Card title="增长率" :loading="loading">
    <div ref="chartRef" :style="{ width, height }" />
  </Card>
</template>
<script lang="ts" setup>
import { useIntervalFn } from "@vueuse/core";
import { Ref, ref, watch } from "vue";
import { Card } from "ant-design-vue";
import { useECharts } from "@pureadmin/utils";

const props = defineProps({
  loading: Boolean,
  width: {
    type: String as PropType<string>,
    default: "100%"
  },
  height: {
    type: String as PropType<string>,
    default: "300px"
  }
});
const chartRef = ref<HTMLDivElement | null>(null);
const { setOptions, getInstance } = useECharts(chartRef as Ref<HTMLDivElement>);

const treeDataURI =
  "data:image/png;base64,";
const beginYear = 2016;
const endYear = 2050;
const lineCount = 10;

// Basic option:
const option = {
  color: ["#e54035"],
  xAxis: {
    axisLine: { show: false },
    axisLabel: { show: false },
    axisTick: { show: false },
    splitLine: { show: false },
    name: beginYear,
    nameLocation: "middle",
    nameGap: -3,
    nameTextStyle: {
      color: "green",
      fontSize: 16,
      fontFamily: "Arial"
    },
    min: -2800,
    max: 2800
  },
  yAxis: {
    data: makeCategoryData(),
    show: false
  },
  grid: {
    top: "center",
    height: 280
  },
  series: [
    {
      name: "all",
      type: "pictorialBar",
      symbol: "image://" + treeDataURI,
      symbolSize: [30, 30],
      symbolRepeat: true,
      data: makeSeriesData(beginYear),
      animationEasing: "elasticOut"
    },
    {
      name: "all",
      type: "pictorialBar",
      symbol: "image://" + treeDataURI,
      symbolSize: [30, 30],
      symbolRepeat: true,
      data: makeSeriesData(beginYear, true),
      animationEasing: "elasticOut"
    }
  ]
};

// Make fake data.
function makeCategoryData() {
  const categoryData = [];
  for (let i = 0; i < lineCount; i++) {
    categoryData.push(i + "a");
  }
  return categoryData;
}

function makeSeriesData(year: number, negative?: boolean) {
  // make a fake value just for demo.
  const r = (year - beginYear + 1) * 6;
  const seriesData = [];

  for (let i = 0; i < lineCount; i++) {
    const sign = negative
      ? -1 * (i % 3 ? 0.8 : 1)
      : 1 * ((i + 1) % 3 ? 0.8 : 1);
    seriesData.push({
      value:
        sign *
        (year <= beginYear + 1
          ? Math.abs(i - lineCount / 2 + 0.5) < lineCount / 5
            ? 2
            : 0
          : (lineCount - Math.abs(i - lineCount / 2 + 0.5)) * r),
      symbolOffset: i % 2 ? ["50%", 0] : undefined
    });
  }
  return seriesData;
}

// Set dynamic data.
let currentYear = beginYear;
useIntervalFn(() => {
  currentYear++;
  if (currentYear > endYear) {
    currentYear = beginYear;
  }
  getInstance()!.setOption({
    xAxis: {
      name: currentYear
    },
    series: [
      {
        data: makeSeriesData(currentYear)
      },
      {
        data: makeSeriesData(currentYear, true)
      }
    ]
  });
}, 800);
watch(
  () => props.loading,
  () => {
    if (props.loading) {
      return;
    }
    setOptions(option);
  },
  { immediate: true }
);
</script>
